import { useState, useEffect } from "react";
import axios from "axios";

const CourseList = () => {
  const [courses, setCourses] = useState([]); // State to store courses

  // Fetch courses from the backend
  useEffect(() => {
    const fetchCourses = async () => {
      try {
        const response = await axios.get("http://localhost:5001/api/courses/all");
        setCourses(response.data); // Store courses in state
      } catch (error) {
        console.error("Error fetching courses:", error);
      }
    };
    fetchCourses();
  }, []);

  return (
    <div>
      <h2>Available Courses</h2>
      {courses.length === 0 ? (
        <p>No courses available</p>
      ) : (
        courses.map((course) => (
          <div key={course._id}>
            <h3>{course.name}</h3>
  
            {course.videos.map((video, index) => {
              let videoId = "";
  
              // Handle different YouTube URL formats
              if (video.includes("youtube.com/watch?v=")) {
                videoId = video.split("v=")[1]?.split("&")[0]; // Extract ID from watch?v=
              } else if (video.includes("youtu.be/")) {
                videoId = video.split("youtu.be/")[1]?.split("?")[0]; // Extract ID from youtu.be/ format
              }
  
              return videoId ? (
                <iframe
                  key={index}
                  width="300"
                  height="200"
                  src={`https://www.youtube.com/embed/${videoId}`}
                  title={`YouTube Video ${index + 1}`}
                  frameBorder="0"
                  allowFullScreen
                ></iframe>
              ) : (
                <p key={index} style={{ color: "red" }}>Invalid YouTube URL</p>
              );
            })}
          </div>
        ))
      )}
    </div>
  );
}
export default CourseList;
