import { useState } from "react";
import axios from "axios";

export default function CreateCourse(){

    //Logic vala part//
    const [courseName, setCourseName] = useState("");
    const [videos, setVideos] = useState([""]);
    const [playlistUrl ,setPlayListUrl] = useState("");

    const handleVideoChange = (index, value)=>{
        const updateVideos = [...videos];
        updateVideos[index] = value;
        setVideos(updateVideos);
    };
    const addMoreVideos = () => setVideos([...videos, ""]);

    const handleSubmit = async ()=>{
        if(!courseName || ((!videos || videos))){
            alert("Course name and all videos are required")
            return;
        }
    
        try{
            await axios.post("http://localhost:5001/api/courses/create",{
                name: courseName,
                videos
            });
            alert("Course created successfully!");
            setCourseName("");
            setVideos([""]);
        }
        catch(error){
            alert("error creating the course");
        }
    }


    //UI rendering

    return (
        <div>
            <h2>Create a course</h2>

            <input
            type = "text"
            placeholder="Type Course Name"
            value = {courseName}
            onChange={(e) => setCourseName(e.target.value)}
            />

            {videos.map((video, index) => (
                            <input
                            key = {index}
                            type ="text"
                            placeholder="Youtube Video Url"
                            value = {video}
                            onChange={(e) => handleVideoChange(index, e.target.value)}
                            />
            ))}


            <button onClick={addMoreVideos}>Add Another Video</button>
            <button onClick={handleSubmit}>Create Course</button>
        </div>
    )
    
}
